import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import Home from "./pages/Home";
import About from "./pages/About";
import BlogList from "./components/Blog/BlogList";
import BlogDetail from "./components/Blog/BlogDetail";
import AddBlog from "./components/Blog/AddBlog";
import Login from "./components/Auth/Login";
import PageNotFound from "./pages/PageNotFound";
import Header from "./components/Layout/Header";
import Footer from "./components/Layout/Footer";
import "./App.css";

const App = () => {
  const isAuthenticated = true; // Replace with your auth logic

  const blogs = [
    { id: 1, title: "First Blog", content: "This is the first blog" },
    { id: 2, title: "Second Blog", content: "This is the second blog" },
  ];

  return (
    <Router>
      <Header />
      <TransitionGroup>
        <CSSTransition timeout={300} classNames="fade" unmountOnExit>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/blogs" element={<BlogList blogs={blogs} />} />
            <Route path="/blogs/:id" element={<BlogDetail />} />
            <Route path="/add-blog" element={isAuthenticated ? <AddBlog /> : <Navigate to="/login" />} />
            <Route path="/login" element={<Login />} />
            <Route path="*" element={<PageNotFound />} />
          </Routes>
        </CSSTransition>
      </TransitionGroup>
      <Footer />
    </Router>
  );
};

export default App;
