import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../../authInterceptor";

const BlogDetail = () => {
  const { id } = useParams(); // Extract the blog ID from the URL
  const navigate = useNavigate();

  const [blog, setBlog] = useState([]);

  // Load saved tasks when the component mounts.
  useEffect(() => {
    getBlogDetail()
  }, [blog]);

  // Updates localStorage every time taskList changes.

  const getBlogDetail = () => {
    api.get('blog/' + id)
      .then(response => {
        if (!response.data.length) {
          return <p>No blogs available. Please add some blogs.</p>;
        }
        setBlog(response.data)
      })
      .catch(error => console.error(error));
  }
  
  return (
    <>

      blog ?

      <div className="blog-detail-container">
        <h2>{blog.title}</h2>
        <p className="blog-author">
          Written by: {blog.author || "Unknown"} | Posted on:{" "}
          {new Date(blog.createdAt).toLocaleDateString()}
        </p>
        <p className="blog-categories">
          Categories: {blog.categories?.join(", ") || "General"}
        </p>
        <p className="blog-content">{blog.content}</p>

        {/* Comments Section */}
        <div className="comments-section">
          <h3>Comments</h3>
          {blog.comments?.length ? (
            blog.comments.map((comment, index) => (
              <div key={index} className="comment">
                <p>
                  <strong>{comment.user}:</strong> {comment.text}
                </p>
              </div>
            ))
          ) : (
            <p>No comments yet. Be the first to comment!</p>
          )}
        </div>

        <button onClick={() => navigate("/blogs")} className="btn-back">
          Back to Blogs
        </button>
      </div> :

      <div className="blog-detail-container">
        <h2>Blog Not Found</h2>
        <p>The blog you are looking for does not exist or has been removed.</p>
        <button onClick={() => navigate("/blogs")} className="btn-back">
          Back to Blogs
        </button>
      </div>
    </>
  );
};

export default BlogDetail;
