import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import Home from './Home';
import BlogDetails from './BlogDetails';
import { Link, Navigate } from "react-router-dom";
import NewPost from './NewPost';
import { TransitionGroup, CSSTransition } from 'react-transition-group';
import Comments from './Comments';
import Login from './Login';
import NotFound from './NotFound';

function PrivateRoute({ element }) {
  const isAuthenticated = !!localStorage.getItem('user');
  return isAuthenticated ? element : <Navigate to="/login" />;
}

function App() {

  const location = useLocation();

  return (
    <div>
      <nav>
        <Link to="/">Home</Link> <br></br>
        <Link to="/blog/12345">Go to my blog BlogDetails</Link><br></br>
        <Link to="/new/post">Add new post</Link><br></br>
      </nav>
      <TransitionGroup>
        <CSSTransition timeout={3000} classNames="slide" key={location.key}>
          <Routes location={location}>
            <Route path="/" element={<Home />} />
            <Route path="/blog/:id" element={<PrivateRoute element={<BlogDetails></BlogDetails>} ></PrivateRoute>} />
            <Route path="/new/post" element={<NewPost />} />
            <Route path="/login" element={<Login />} />
            <Route path="*" element={<NotFound></NotFound>} />
          </Routes>
        </CSSTransition>
      </TransitionGroup>
    </div>
  );
}

const WrapperApp = () => (
  <Router>
    <App />
  </Router>
);

export default WrapperApp;
